    )
        internal
        view
        returns (
            bool exist,
            uint128 indexValue,
            uint128 totalUnitsApproved,
            uint128 totalUnitsPending
        )
    {
        return idaLibrary.ida.getIndex(token, publisher, indexId);
    }

    /// @dev Calculates the distribution amount based on the amount of tokens desired to distribute.
